export interface BudgetsState {
  budgets: string[]
}

const initialState = {
  budgets: [],
}

export type Action = { type: "ADD_BALANCE"; payload: string };

export const budgetsReducer = (
  state: BudgetsState = initialState,
  action: Action
) => {
  if (action.type === 'ADD_BALANCE') {
    return {
      ...state, 
      budgets: [...state.budgets, action.payload],
    }
  }
  return state
}
