export interface BudgetsState {
  budgets: Array<any>
}

const initialState = {
  // budgets: [],
  budgets: [
    { item: 'Site Preparation', units: 25, cost_per_unit: 10, total_cost: 250 },
    { item: 'Excavation', units: 25, cost_per_unit: 10, total_cost: 250 },
    { item: 'Plumbing', units: 40, cost_per_unit: 20, total_cost: 800 },
    { item: 'Telecom System', units: 2, cost_per_unit: 16000, total_cost: 500 },
    { item: 'Debris Removal', units: 1, cost_per_unit: 10, total_cost: 500 },
  ],
}

export type Action = { type: 'ADD_BUDGET_DATA', payload: string }

const budgetsReducer = (
  state: BudgetsState = initialState,
  action: Action
) => {
  if (action.type === 'ADD_BUDGET_DATA') {
    return {
      ...state, 
      budgets: [...state.budgets, action.payload],
    }
  }
  return state
}

export default budgetsReducer
