export interface BudgetsState {
  budgets: Array<any>
}

const initialState = {
  // budgets: [],
  budgets: [
    { item: 'Telecom System', units: 1, cost_per_unit: 10, total_cost: 500 },
    { id: 2, units: 25, cost_per_unit: 10, total_cost: 250 },
    { id: 3, units: 40, cost_per_unit: 20, total_cost: 800 },
  ],
}

export type Action = { type: "ADD_BUDGET_DATA"; payload: string };

const budgetsReducer = (
  state: BudgetsState = initialState,
  action: Action
) => {
  if (action.type === 'ADD_BUDGET_DATA') {
    return {
      ...state, 
      budgets: [...state.budgets, action.payload],
    }
  }
  return state
}

export default budgetsReducer
