export interface CollateralState {
  collateral: any
}

const initialState = {
  collateral: [
    { year: 2016, hedge_funds: 1.2, securities: 3.1, volume: 6.1, price: 1.8 },
    { year: 2017, hedge_funds: 1.5, securities: 3.3, volume: 7.5, price: 2.0 },
    { year: 2018, hedge_funds: 1.6, securities: 3.7, volume: 8.1, price: 2.2 },
    { year: 2019, hedge_funds: 1.5, securities: 3.9, volume: 8.5, price: 2.2 },
  ],
}

export type Action = { type: 'ADD_COLLATERAL_DATA', payload: any }

const collateralReducer = (
  state: CollateralState = initialState,
  action: Action
) => {
  if (action.type === 'ADD_COLLATERAL_DATA') {
    return {
      ...state, 
      collateral: [...state.collateral, action.payload],
    }
  }
  return state
}

export default collateralReducer
