export interface CollateralState {
  collateral: any
}

const initialState = {
  collateral: [
    { debt: 'Senior', maturity: 'Apr 2018', rating: 'AAA', yield: '5.27%', price: '1150.32' },
    { debt: 'Senior', maturity: 'Nov 2018', rating: 'AA', yield: '5.44%', price: '1180.24' },
    { debt: 'Mezzanine', maturity: 'Jan 2019', rating: 'BBB', yield: '5.63%', price: '1020.37' },
    { debt: 'Junior', maturity: 'Jun 2019', rating: 'B', yield: '5.79%', price: '1011.88' },
  ],
}

export type Action = { type: 'ADD_COLLATERAL_DATA', payload: any }

const collateralReducer = (
  state: CollateralState = initialState,
  action: Action
) => {
  if (action.type === 'ADD_COLLATERAL_DATA') {
    return {
      ...state, 
      collateral: [...state.collateral, action.payload],
    }
  }
  return state
}

export default collateralReducer
