export interface BudgetsState {
  budgets: Array<any>
}

const initialState = {
  budgets: [
    { units: 50, cost_per_unit: 10, total_cost: 500 },
    { units: 25, cost_per_unit: 10, total_cost: 250 },
    { units: 40, cost_per_unit: 20, total_cost: 800 }
  ],
}

export type Action = { type: "ADD_BALANCE"; payload: string };

const budgetsReducer = (
  state: BudgetsState = initialState,
  action: Action
) => {
  if (action.type === 'ADD_BALANCE') {
    return {
      ...state, 
      budgets: [...state.budgets, action.payload],
    }
  }
  return state
}

export default budgetsReducer
